import React from 'react';
import { useState } from 'react';
import { FontAwesomeIcon } from "C:/Users/Bhardwaj/Documents/work/Learning/REACT/react-player/node_modules/@fortawesome/react-fontawesome";
import {faPlay,faPause,faAngleLeft,faAngleRight} from '@fortawesome/free-solid-svg-icons';

const Player = ({audioRef,currentSong,setIsPlaying,isPlaying,setSongInfo,songInfo}) => {
    //Event Handlers
    const playSongHandler = () => {
        if (isPlaying) {
            audioRef.current.pause();
            setIsPlaying(!isPlaying);
        }else{
            audioRef.current.play();
            setIsPlaying(!isPlaying);
        }
    };
    const timeUpdateHandler = (e) => {
        const current = e.target.currentTime;
        const duration = e.target.duration;
        setSongInfo({...songInfo,currentTime: current, duration});
    };
    const getTime = (time) => {
        return(
            Math.floor(time / 60) + ":" + ("0" + Math.floor(time % 60)).slice(-2)
        );
    };
    const dragHandler = (e) => {
        audioRef.current.currentTime = e.target.value;
        setSongInfo({...songInfo, currentTime: e.target.value});
    };
    return(
        <div className="player">
            <div className="time-control">
                <p><b>{getTime(songInfo.currentTime)}</b></p>
                <input min={0} max={songInfo.duration} onChange={dragHandler} value={songInfo.currentTime} type="range"  />
                <p><b>{getTime(songInfo.duration)}</b></p>
            </div>
            <div className="play-control">
                <FontAwesomeIcon className="skip-back" size="2x" icon={faAngleLeft} /> 
                <FontAwesomeIcon
                    onClick={playSongHandler} 
                    className="play" 
                    size="2x" 
                    icon={isPlaying ? faPause : faPlay}
                    />
                <FontAwesomeIcon className="skip-back" size="2x" icon={faAngleRight} />
            </div>
        </div>
    );
};

export default Player;
