import React, {useRef} from 'react';
import { useState } from 'react';
import {IoPlayOutline,IoPlaySkipForwardOutline,IoPlaySkipBackOutline} from "react-icons/all";

const Player = ({currentSong,setIsPlaying,isPlaying}) => {
    //Ref
    const audioRef = useRef(null);
    //Event Handlers
    const playSongHandler = () => {
        if (isPlaying) {
            audioRef.current.pause();
            setIsPlaying(!isPlaying);
        }else{
            audioRef.current.play();
            setIsPlaying(!isPlaying);
        }
    };
    const timeUpdateHandler = (e) => {
        const current = e.target.currentTime;
        const duration = e.target.duration;
        console.log(duration);
        setSongInfo({...songInfo,currentTime: current, duration});
    };
    const getTime = (time) => {
        return(
            Math.floor(time / 60) + ":" + ("0" + Math.floor(time % 60)).slice(-2)
        );
    };
    const dragHandler = (e) => {
        onChange={dragHandler};
    };
    //State
    const [songInfo,setSongInfo] = useState({
        currentTime: null,
        duration: null,
    });
    return(
        <div className="player">
            <div className="time-control">
                <p><b>{getTime(songInfo.currentTime)}</b></p>
                <input min={0} max={songInfo.duration} onChange={dragHandler} value={songInfo.currentTime} type="range"  />
                <p><b>{getTime(songInfo.duration)}</b></p>
            </div>
            <div className="play-control">
                <IoPlaySkipBackOutline className="skip-back" size="2rem" icon={IoPlaySkipBackOutline}/> 
                <IoPlayOutline onClick={playSongHandler} className="play" size="2rem" icon={IoPlayOutline}/>
                <IoPlaySkipForwardOutline className="skip-back" size="2rem" icon={IoPlaySkipForwardOutline}/>
            </div>
            <audio onLoadedMetadata={timeUpdateHandler} onTimeUpdate={timeUpdateHandler} ref={audioRef} src={currentSong.audio}></audio>
        </div>
    );
};

export default Player;
